import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import Test from "../../components/test.js";

const RegistrationPage = () => {
  const { user, isAuthenticated } = useAuth0();
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    // Automatically try to register the user when they are authenticated
    if (isAuthenticated && user) {
      handleSubmit();
    }
  }, [user, isAuthenticated]);

 const handleSubmit = async () => {
  setIsSubmitting(true);
  try {
    const response = await fetch('http://localhost:8080/add_user', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        auth0_id: user.sub, // Make sure this matches the expected parameter in your SQL statement
        email: user.email,
        // ... include other fields if necessary, with appropriate default values
        hobbies: '[]',
        tasks: '[]',
        emoji: '',
        date_of_birth: ''
      })
    });
    const data = await response.json();
    console.log(data.message);
    // Redirect or display success message
  } catch (error) {
    console.error('Error submitting user info:', error);
  }
  setIsSubmitting(false);
};

  return (
    <div>
       <Test/>

      {isAuthenticated ? (
        <button onClick={handleSubmit} disabled={isSubmitting}>
          {isSubmitting ? 'Submitting...' : 'Register'}
        </button>
      ) : (
        <p>Please log in to register.</p>
      )}
    </div>
  );
};

export default RegistrationPage;
