import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import fetchArticles from '../../components/newsAPI';
import fetchTopBooks from '../../components/bookAPI'; 
import './Recommendations.css';

const Recommendations = () => {
  const { isAuthenticated, user, isLoading: authLoading } = useAuth0();
  const [articles, setArticles] = useState([]);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (isAuthenticated) {
      const fetchData = async () => {
        setLoading(true);
        const articlesResult = await fetchArticles(user);
        setArticles(articlesResult);

        const booksResult = await fetchTopBooks(user);
        setBooks(booksResult);

        setLoading(false);
      };

      fetchData();
    }
  }, [isAuthenticated, user]);

  if (authLoading) {
    return <div>Loading...</div>;
  }

  if (!isAuthenticated) {
    // Potentially redirect to login or show a message
    return <div>User is not authenticated</div>;
  }

  return (
    <div className="recommendations-page">
      <nav className="navigation-bar">
        <ul>
          <li><a href="/loggedinpage">Home</a></li>
          <li><a href="/tasks">Tasks</a></li>
          <li><a href="/points">Points</a></li>
          <li><a href="/recommendations">Recommendations</a></li>
          <li><a href="/profilepage">Profile</a></li>
        </ul>
      </nav>
      <div className="recommendations-content">
        <h1>Top Ten Best-Selling Books:</h1>
        {loading ? <p>Loading books...</p> : (
          <ul>
            {books.map((book, index) => (
              <li key={index}>
                <h2>{book.title}</h2>
                <p>{book.description}</p>
                <p>Author: {book.author}</p>
              </li>
            ))}
          </ul>
        )}
        <h1>Top News Headlines:</h1>
        {loading ? <p>Loading articles...</p> : (
          <ul>
            {articles.map((article, index) => (
              <li key={index}>
                <h2>{article.title}</h2>
                <p>{article.description}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Recommendations;
