import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

const RegistrationPage = () => {
  const { user, isAuthenticated } = useAuth0();
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    // Automatically try to register the user when they are authenticated
    if (isAuthenticated && user) {
      handleSubmit();
    }
  }, [user, isAuthenticated]);

  const handleSubmit = async () => {
    setIsSubmitting(true); // Indicate that submission is in progress
    try {
      const response = await fetch('http://localhost:8080/add_user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          auth0_id: user.sub, // Auth0 user ID
          email: user.email // User's email from Auth0
        })
      });
      const data = await response.json();
      console.log(data.message);
      // Redirect or display success message
    } catch (error) {
      console.error('Error submitting user info:', error);
      // Handle the error
    }
    setIsSubmitting(false); // Indicate that submission is done
  };

  return (
    <div>
      {isAuthenticated ? (
        <button onClick={handleSubmit} disabled={isSubmitting}>
          {isSubmitting ? 'Submitting...' : 'Register'}
        </button>
      ) : (
        <p>Please log in to register.</p>
      )}
    </div>
  );
};

export default RegistrationPage;
