import React, { useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

const RegistrationPage = () => {
  const { user, isAuthenticated } = useAuth0();

  useEffect(() => {
    // Automatically register the user when they are authenticated
    if (isAuthenticated && user) {
      handleSubmit(user.sub, user.email);
    }
  }, [user, isAuthenticated]);

  const handleSubmit = async (user_id, email) => {
    try {
      const response = await fetch('http://localhost:8080/add_basic_user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          user_id: user_id,
          email: email
        })
      });
      const data = await response.json();
      console.log(data.message);
      // Redirect or display success message
    } catch (error) {
      console.error('Error submitting user info:', error);
    }
  };

  return (
    <div>
      {isAuthenticated ? (
        <p>Registration in progress...</p>
      ) : (
        <p>Please log in to register.</p>
      )}
    </div>
  );
};

export default RegistrationPage;
